        for(var i=firstFeatureChunk; i<=lastFeatureChunk; i++) {
            if(i >= firstRegionChunk && i<= lastRegionChunk){//only if is inside the called region
                key = region.chromosome+":"+i;
                this.store.put()
                this.cache[key][dataType].push(gzipFeature);
            }
        }
